    background-color: #fff;
    background-size: cover;
    -moz-background-size: cover;
    -webkit-backgroundsize: cover;
    backgroundattachment: scroll;
    top: auto;
    bottom: 0;
    position: relative;
}
.main-banner{
    background-image: 
